mapper.update:
update emp
set username = #{username},name = #{name},gender = #{gender},image = #{image},job = #{job},entrydate = #{entrydate},dept_id = #{deptId},update_time = now()
where id = #{id}

mapper.select:
select * from emp

     name like concat('%',#{name},'%')
    and  gender = #{gender}
    and  entrydate between  #{begin} and #{end}


 limit #{page},#{pageSize}
